// Function to get the IST Date and Time from any location.
function GetISTDateAndTime(time = null) {
    var date = new Date();
    var UTFoffset = date.getTimezoneOffset();
    date.setMinutes(date.getMinutes() + UTFoffset);
    var IST = 5.5 * 60;
    date.setMinutes(date.getMinutes() + IST);
    return time === 'time'  ? date.toLocaleString() : date.toLocaleDateString();
}
exports.GetISTDateAndTime = GetISTDateAndTime;
